define([
  {"re" : /[a-z]/, "score" : 1}, // 至少一个小写字母 +1
  {"re" : /[A-Z]/, "score" : 5}, // 至少一个大写字母 +5
  {"re" : /\d+/, "score" : 5}, // 至少一个数字 +5
  {"re" : /(\d.*\d.*\d)/, "score" : 5}, // 至少三个数字 +5
  {"re" : /[!,@#$%^&*?_~]/, "score" : 5}, // 至少一个特殊字符 +5
  {"re" : /([!,@#$%^&*?_~].*[!,@#$%^&*?_~])/, "score" : 5}, // 至少两个特殊字符 +5
  {"re" : [/[a-z]/, /[A-Z]/], "score" : 2}, // 大写和小写同时存在 +2
  {"re" : [/\d/ ,/\D/], "score" : 2} // 字母和数字同时存在 +2
]);